import { createStore } from "vuex"
import axios from "axios"

import navBarList from "./assets/navBarMenu.js"


const BASE_URL = "https://reqres.in/"

const store = createStore({
    state(){ return {
        isLogin : false,
        currentUserData : {
            name : "박희찬",
            year : 1,
            class : 4,
            number : 13,

            profileImg : "data:image/png;base64,"
        },
        authToken : null,

        navBarList,
    }
    },
    mutations :{
        loginSuccess(state, userData){
            state.currentUserData = userData
            state.isLogin = true
        }
    },
    actions : {
        login(context, loginData){
            axios.post(BASE_URL + "api/login", { "email" : loginData.email, "password" : loginData.password})
                .then(res => {
                    const token = res.data.token
                    localStorage.setItem('auth_token', token)
                    context.dispatch("getUserData")
                })
                .catch(() => {
                    alert("로그인에 실패했습니다.")
                })
        },
        getUserData(context){
            const token = localStorage.getItem("auth_token")

            const config = {
                header : {"access-token" : token}
            }
            
            axios.get(BASE_URL + "api/users/2", config)
                .then(result => {
                    const userData = result.data.data
                    context.commit("loginSuccess", userData)
                })
                .catch(() => {
                    alert("로그인에 실패했습니다.")
                })
        }
    }
})

export default store